CREATE TABLE IF NOT EXISTS `Dim_Waktu` (
    `id_waktu` INT PRIMARY KEY,
    `tahun` INT NOT NULL,
    `kuartal` INT,
    `bulan` INT,
    `tanggal` DATE
);

CREATE TABLE IF NOT EXISTS `Dim_Gender` (
    `id_gender` INT AUTO_INCREMENT PRIMARY KEY,
    `gender` CHAR(1) NOT NULL
);

CREATE TABLE IF NOT EXISTS `Dim_Departemen` (
    `id_departemen` INT AUTO_INCREMENT PRIMARY KEY,
    `department` VARCHAR(10) NOT NULL,
    `department_name` VARCHAR(255) NOT NULL,
    `division` VARCHAR(255) NOT NULL,
    UNIQUE (`department`, `department_name`, `division`)
);

CREATE TABLE IF NOT EXISTS `Dim_Grade` (
    `id_grade` INT AUTO_INCREMENT PRIMARY KEY,
    `grade` VARCHAR(10) NOT NULL UNIQUE
);

CREATE TABLE IF NOT EXISTS `Fact_GajiKaryawan` (
    `id_fakta_gaji` INT AUTO_INCREMENT PRIMARY KEY,
    `id_gender` INT NOT NULL,
    `id_departemen` INT NOT NULL,
    `id_grade` INT NOT NULL,
    `id_waktu` INT NOT NULL,
    `base_salary` DECIMAL(10, 2) NOT NULL,
    `overtime_pay_2024` DECIMAL(10, 2) NOT NULL,
    `longevity_pay_2024` DECIMAL(10, 2) NOT NULL,
    `total_gaji` DECIMAL(10, 2) NOT NULL,

    FOREIGN KEY (`id_karyawan`) REFERENCES `Dim_Karyawan`(`id_karyawan`),
    FOREIGN KEY (`id_departemen`) REFERENCES `Dim_Departemen`(`id_departemen`),
    FOREIGN KEY (`id_grade`) REFERENCES `Dim_Grade`(`id_grade`),
    FOREIGN KEY (`id_waktu`) REFERENCES `Dim_Waktu`(`id_waktu`)
);



CREATE TABLE IF NOT EXISTS `employees` (
    `id` INT AUTO_INCREMENT PRIMARY KEY,
    `department` VARCHAR(10),
    `department_name` TEXT,
    `division` TEXT,
    `gender` CHAR(1),
    `base_salary` DECIMAL(10, 2),
    `_2024_overtime_pay` DECIMAL(10, 2),
    `_2024_longevity_pay` DECIMAL(10, 2),
    `grade` VARCHAR(10)
);
